      (error) => {
        const _content =
          (error.response && error.response.data) ||
          error.message ||
          error.toString();

       // setContent(_content);
      }
    );

  };
  useEffect(() => {
    getallrestaurant();
  }, []);


  function handleChange(e) {
    setText(e.target.value);
   }
  return (
    <>
    <div className="container">
      <header className="jumbotron">



      <h2> All Restaurant </h2>
<input type="text" onChange={handleChange} />
      <div className="container row">

      {ProductDetail &&
              ProductDetail.map((exhibition, i) => {
                return (

<div className="col-md-4"> 

<div class="card img-fluid" style={{"width":"500px"}}>
    <img className="card-img-top" src={ exhibition.img} alt="Card image" style={{"width":"100%"}} />
    <h4 className="card-title">{exhibition.name}</h4>
    <a href="#" className="btn btn-primary"> Price {exhibition.price }  </a>


  </div>

</div>


                 );
              })}




</div>


      </header>
    </div>

    </>

  );
};

export default Home;
